import Foundation
import Moya

/// OP.GG API Test API 명세
public enum OPGGTestAPI {
    
    /// 소환사 정보
    case summoner(name: String)
    
    /// 게임 정보
    case matches(name: String, lastMatch: TimeInterval? = nil)
}

extension OPGGTestAPI: TargetType {
    
    public var baseURL: URL {
        let string = "https://codingtest.op.gg"
        
        assert(URL(string: string) != nil)
        return URL(string: string)!
    }
    
    public var path: String {
        switch self {
        case .summoner(let name):
            assert(name.addingPercentEncoding(withAllowedCharacters: .urlPathAllowed) != nil)
            return "/api/summoner/\(name.addingPercentEncoding(withAllowedCharacters: .urlPathAllowed)!)"
        case .matches(let name, _):
            assert(name.addingPercentEncoding(withAllowedCharacters: .urlPathAllowed) != nil)
            return "/api/summoner/\(name.addingPercentEncoding(withAllowedCharacters: .urlPathAllowed)!)/matches"
        }
    }
    
    public var method: Moya.Method {
        .get
    }   
    
